() => (
  <RN.FlatList
    data={useData("scA0.gameList") || []}
    keyExtractor={(item) => item.gameID}
    renderItem={({ item }) => {
      const wishlist = useData("sc.A1.listed") || [];

      const alreadyInWishlist = wishlist.some((w) => w.gameID === item.gameID);

      return (
        <RN.View
          style={{
            padding: 10,
            marginBottom: 10,
            borderWidth: 1,
            borderColor: "#ccc",
            borderRadius: 6,
          }}
        >
          <RN.Text style={{ fontWeight: "bold" }}>{item.title}</RN.Text>
          <RN.Text>
            Preço: ${item.salePrice} (Normal: ${item.normalPrice})
          </RN.Text>

          {alreadyInWishlist ? (
            <RN.Text style={{ color: "green", marginTop: 8 }}>
              Já está na Wishlist ✓
            </RN.Text>
          ) : (
            <RN.Pressable
              style={{
                marginTop: 8,
                backgroundColor: "#007bff",
                padding: 10,
                borderRadius: 5,
              }}
              onPress={() => {
                const path2 = "sc.A1.wishlist";
                const pass = { keyPath: [path2], value: item };
                tools.functions.setVar({ args: "", pass });
              }}
            >
              <RN.Text style={{ color: "#fff", textAlign: "center" }}>
                Adicionar à Wishlist
              </RN.Text>
            </RN.Pressable>
          )}
        </RN.View>
      );
    }}
  />
);
