1750692903874.oldValue:
() => {
  const [games, setGames] = React.useState([]);
  const [loading, setLoading] = React.useState(false);

  const fetchDeals = async (filterType) => {
    setLoading(true);
    let url = "https://www.cheapshark.com/api/1.0/deals?";

    switch (filterType) {
      case "store":
        url += "storeID=1"; // Steam por exemplo
        break;
      case "lowest":
        url += "sortBy=Price";
        break;
      case "discount":
        url += "sortBy=Deal%20Rating";
        break;
      default:
        break;
    }

    try {
      const res = await fetch(url);
      const data = await res.json();
      setGames(data);
    } catch (error) {
      console.error("Erro ao buscar dados:", error);
    } finally {
      setLoading(false);
    }
  };

const buttonStyle = {
    backgroundColor: "#212E1F",
    borderRadius: 10,
    padding: 5,
    alignItems: "center",
    marginBottom: 2,
  };

  const buttonTextStyle = {
    color: "#FFF",
    fontSize: 10,
  };

  return (
    <RN.View style={{ padding: 1 }}>
      <RN.Text style={{ color: "#FFF", fontSize: 12, marginBottom: 8 }}>
        Filtros:
      </RN.Text>

      <RN.Pressable style={buttonStyle} onPress={() => fetchDeals("store")}>
        <RN.Text style={buttonTextStyle}>Filtrar por Loja (Steam)</RN.Text>
      </RN.Pressable>

      <RN.Pressable style={buttonStyle} onPress={() => fetchDeals("lowest")}>
        <RN.Text style={buttonTextStyle}>Menor Preço</RN.Text>
      </RN.Pressable>

      <RN.Pressable style={buttonStyle} onPress={() => fetchDeals("discount")}>
        <RN.Text style={buttonTextStyle}>Maior Desconto</RN.Text>
      </RN.Pressable>


      {loading ? (
        <RN.ActivityIndicator size="large" />
      ) : (
        <RN.FlatList
          data={games}
          keyExtractor={(item) => item.dealID}
          renderItem={({ item }) => {
            const price =
              "Preço: $" +
              item.salePrice +
              "Normal: $" +
              item.normalPrice +
              ")";
            const discount =
              "Desconto:" +
              Math.round(
                ((item.normalPrice - item.salePrice) / item.normalPrice) * 100
              ) +
              "%";

            return (
              <RN.View
                style={{
                  marginBottom: 10,
                  padding: 10,
                  borderWidth: 1,
                  borderColor: "#ccc",
                  borderRadius: 6,
                }}
              >
                <RN.Text style={{ fontWeight: "bold", fontSize: 16 }}>
                  {item.title}
                </RN.Text>
                <RN.Text>{price}</RN.Text>
                <RN.Text>{discount}</RN.Text>
              </RN.View>
            );
          }}
        />
      )}
    </RN.View>
  );
}

